import React, { useState } from "react";

const ChangePassword = () => {
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const handleChange = () => {
        // Implement your password change logic here
        // You can access oldPassword, newPassword, and confirmPassword state values
        // Validate the input and perform necessary actions
        console.log("Password change clicked!");
    };

    return (
        <div className="flex flex-col items-center justify-center mt-8">
            <p className="text-2xl font-bold mb-4">Change Password</p>
            <input
                type="password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="Your Old Password"
            />
            <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="New Password"
            />
            <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="Confirm Password"
            />
            <button
                type="button"
                onClick={handleChange}
                className="w-[80%] max-w-md p-2 bg-orange-500 text-white rounded-md cursor-pointer"
            >
                Submit
            </button>
        </div>
    );
};

export default ChangePassword;
