import React from "react";
import img from './imgs/plateimg.svg'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckCircle } from '@fortawesome/free-solid-svg-icons';
const AboutUs =() =>{
    return (
        <div class="md:h-[40vh] lg:h-[75vh] h-[30%] mt-12 bg-orange-50 w-[96%] ml-[2%] pt-10">
            <div class="">
                <p class='font-semibold text-2xl'>About Us</p>
            </div>
            <div class="grid grid-flow-col">
                <div class="w-[65%]  bg-white ml-[5%] mt-[5%] h-[90%] shadow-lg rounded ">
                    <p class="font-normal text-left mx-3 pt-2">
                    Welcome to <hn class="text-orange-700" >FlavZ</hn> the ultimate destination for culinary enthusiasts and home cooks!
                    </p>
                    <div class="ml-6 text-left mt-5">
                        <p> <FontAwesomeIcon icon={faCheckCircle} className="text-green-700 text-left" /> Implement a recommendation engine that suggests personalized recipes to users based on their past preferences</p>
                        <p><FontAwesomeIcon icon={faCheckCircle} className="text-green-700 text-left" />Enhance the recipe pages with interactive cooking tutorials, step-by-step guides, and helpful cooking tips</p>
                        <p><FontAwesomeIcon icon={faCheckCircle} className="text-green-700 text-left" />Offer exclusive discounts, promotions, or loyalty rewards to users who regularly engage with your platform.</p>
                    </div>
                </div>
                <div class="w-[100%] h-[100%] ">
                    <img src={img} class=" mt-[5%] mr-[10%]"></img>
                </div>
            </div>
        </div>
    )
};
export default AboutUs ;
