import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import loginBgUrl from '../imgs/loginbg.webp';

const LoginComp = () => {
    const [action, setAction] = useState("Sign Up");
    const [UserName, setUserName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        // Set the action to "Login" when the component mounts
        setAction("Login");
    }, []); // Empty dependency array ensures this runs only once

    function handleSubmit(event) {
        event.preventDefault();
        console.log("hehehe", action);
        if (action === "Sign Up") {
            console.log(UserName);
            console.log(email);
            console.log(password);
        } else {
            console.log(UserName);
            console.log(password);
        }
    }

    return (
        <div style={{
            backgroundImage: `url(${loginBgUrl})`,
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            width: '99%',
        }} className="my-5 md:mx-2 h-[80vh] md:h-[100vh] rounded flex m-auto justify-center items-center">

            <div className={`container bg-orange-50  h-fit lg:w-[50vw] sm:w-[70vw] w-full rounded flex flex-col m-auto md:mt-auto mt-20 `}>
                <div className="flex flex-col gap-8  items-center md:mt-[8vh] mt-[2vh]">
                    <div className="text text-orange-500 text-4xl font-semibold">{action}</div>
                    <form onSubmit={handleSubmit}>
                        {action === "Sign Up" ? (
                            <div className=" mt-10 flex flex-col gap-5">
                                {/* ... (rest of your Sign Up form) */}
                            </div>
                        ) : (
                            <div className=" mt-10 flex flex-col gap-5">
                                {/* ... (rest of your Login form) */}
                            </div>
                        )}

                        <div className="submit-container flex gap-7 mx-auto mb-5">
                            <div className={` ${action === "Sign Up" ? "bg-[#E1611F] shadow-orange-100 border-orange-600 text-white" : "bg-gray-300 shadow-gray-100 border-gray-600 text-black "} my-2 border w-full h-10 cursor-pointer transition-all px-6 py-2 rounded-lg border-b-[4px] hover:brightness-110 hover:-translate-y-[1px] hover:border-b-[6px] active:border-b-[2px] active:brightness-90 active:translate-y-[2px] md:max-w-[70%] justify-center flex text-center`}>
                                {console.log(action)}
                                <button type={action === "Sign Up" ? "button" : "submit"} onClick={() => setAction("Sign Up")}>Sign Up</button>
                            </div>
                            <div className={` ${action === "Sign Up" ? "bg-[#E1611F] shadow-orange-100 border-orange-600 text-white" : "bg-gray-300 shadow-gray-100 border-gray-600 text-black "} my-2 border w-full h-10 cursor-pointer transition-all px-6 py-2 rounded-lg border-b-[4px] hover:brightness-110 hover:-translate-y-[1px] hover:border-b-[6px] active:border-b-[2px] active:brightness-90 active:translate-y-[2px] md:max-w-[70%] justify-center flex text-center`}>
                                {console.log(action)}
                                <button type={action === "Sign Up" ? "button" : "submit"} onClick={() => setAction("Login")}>Login</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default LoginComp;
