import React, { useState } from "react";
import './index.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faBagShopping, faUser, faBars, faX } from '@fortawesome/free-solid-svg-icons';

const Nav = () => {
    const [menuIcon, setMenuIcon] = useState(faBars);
    const [menuVisible, setMenuVisible] = useState("hidden");

    const onToggleMenu = () => {
        if (menuIcon === faBars) {
            setMenuIcon(faX);
            setMenuVisible("flex");
        } else {
            setMenuIcon(faBars);
            setMenuVisible("hidden");
        }
    };

    return (
        <nav className="h-20 bg-gray-100 no-underline flex items-center ">
            <label className="font-extrabold w-full text-left text-3xl text-[#E1611F] pl-8  ">FlavZ</label>
            <div id="navLinks" className={`absolute duration-500 md:static ${menuVisible} md:flex z-10  md:bg-gray-100 bg-white min-h-full absolute inset-y-0 md:min-h-fit left-0 top-[9%] w-full items-center px-5 `}>
                <ul className="flex md:flex-row flex-col md:items-center text-center w-full text-[#373743] gap-8 md:gap-[4vw] ">
                    <li className="hover:underline hover:text-[#656C7B]"><a href="/">Recipies</a></li>
                    <li className="hover:underline hover:text-[#656C7B]"><a href="/">About Us</a></li>
                    <li className="hover:underline hover:text-[#656C7B]"><a href="/">Contact</a></li>
                </ul>
            </div>
            <ul className="flex items-center gap-[2vw] px-8 ml-28" >
                <li className="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faMagnifyingGlass} /></a></li>
                <li className="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faBagShopping} /></a></li>
                <li className="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faUser} /></a></li>
            </ul>
            <div>
                <label className="text-[#E1611F] list-none mr-4 cursor-pointer text-3xl md:hidden">
                    <button onClick={onToggleMenu}><FontAwesomeIcon icon={menuIcon} /></button>
                </label>
            </div>
        </nav>
    );
};

export default Nav;
