import React ,{useState ,useEffect} from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const WhatToCockToday=() =>{
    const apiKey='1';
    const [Meals, setMeals] = useState([]);
    let [apiUrl , setApiUrl]=useState("https://www.themealdb.com/api/json/v1/${apiKey}/latest.php");
    const [category ,setCategory]=useState("");
    
    useEffect(() => {
        const GetMeals = async (category) => {
            try {
                const response = await fetch(apiUrl);

                if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const data = await response.json();

                setMeals(data.categories);
                console.log(data.categories);
                console.log(apiUrl)
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        GetMeals(category);
    }, [apiUrl ,category]);
    const handlePopularity =() =>{
        setApiUrl("https://www.themealdb.com/api/json/v1/${apiKey}/latest.php");
    }
    const handleOther =(category) =>{
        setApiUrl("https://www.themealdb.com/api/json/v1/${apiKey}/latest.php");
        setCategory(category)
    }
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 1500,
        pauseOnHover: true,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: false
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2,
              dots:false
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              dots:false
            }
          }
        ]
      };
    return (
        <div>
            <div>
                <p class="text-xl">What To Cock Today </p>
                <p class="text-sm text-gray-600">Fast ,Fresh , and FoolProof</p>
            </div>
            <div>
                <button onClick={handlePopularity} >Popularity</button>
                <button onClick={() => handleOther("Pasta")}>Pasta</button>
                <button onClick={() => handleOther("Vegeterian")}>Vegeterian</button>
                <button onClick={() => handleOther("Dessert")} >Dessert</button>
            </div>
            <div>
                <Slider {...settings}>
                    {Meals.map((Meal) => (
                    <div key={Meal.idCategory} className="w-52 h-36 ">
                        <img src={Meal.strCategoryThumb}className="" />
                        <p></p>
                    </div>
                    ))}
                </Slider>
            </div>
        </div>
    )
};
export default WhatToCockToday;
