import axios from "axios";
import React, { useState } from "react";

function handleChange() {
    if (oldPassword !== props.password) {
        console.log("bling");
        setError("Old password is incorrect");
        return; // Stop further execution if old password is incorrect
    }

    if (!oldPassword || !newPassword || !confirmPassword) {
        setError("Please fill in all fields");
        return; // Stop further execution if any field is empty
    }

    if (newPassword !== confirmPassword) {
        setError("New password and confirm password must match");
        return; // Stop further execution if passwords don't match
    }

    setError("");
    axios.put('/api/changePassword', {
        name: props.name,
        newPassword: newPassword,
        oldPassword: oldPassword
    }, {
        headers: {
            'Content-Type': 'application/json',
        },
    })
    .then(res => {
        console.log(res);
    })
    .catch(error => {
        console.error("Error updating password from the client side:", error);
    });

    console.log("Password change logic executed!");



    return (
        <div className="flex flex-col items-center justify-center mt-8">
            <p className="text-2xl font-bold mb-4">Change Password</p>
            <input
                type="password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="Your Old Password"
            />
            <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="New Password"
            />
            <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-[80%] max-w-md p-2 border rounded-md mb-2"
                placeholder="Confirm Password"
            />
            <button
                type="button"
                onClick={handleChange}
                className="w-[80%] max-w-md p-2 bg-orange-500 text-white rounded-md cursor-pointer"
            >
                Submit
            </button>
            {error && <p className="text-red-500 mt-2">{error}</p>}
        </div>
    );
};

export default ChangePassword;
