import React, { useEffect, useState } from "react";
import '../../index.css';
import food1 from '../imgs/food1.avif';
import food2 from '../imgs/food2.avif';
import { Link } from "react-router-dom";

const PopularRecipes = () => {
    const [meals, setMeals] = useState([]);

    useEffect(() => {
        const getMeals = async () => {
            const m = [];
            for (let i = 0; i < 10; i++) {
                const apiUrl = "https://www.themealdb.com/api/json/v1/1/random.php";
                try {
                    const response = await fetch(apiUrl);

                    if (!response.ok) {
                        throw new Error(`HTTP error! Status: ${response.status}`);
                    }

                    const data = await response.json();

                    m.push(data.meals[0]);
                } catch (error) {
                    console.error('Error fetching data:', error.message);
                }
            }
            setMeals(m);
        };

        getMeals();
    }, []);

    return (
        <div className="flex flex-col-2 md:h-[40vh] lg:h-[80vh] h-[35%] md:mt-12 mt-8 md:mb-6 bg-white  ">
            {/* ... (rest of your code) */}
            <Link to={`/Recipe/${meals.length > 0 ? meals[0].idMeal : ''}`}>
                {/* Assuming you want the first meal's id */}
                <button className="my-2  
                                border 
                                // ... (rest of your button styles)
                                " onClick={() => getMeals()}>
                    Random Meal
                </button>
            </Link>
            {/* ... (rest of your code) */}
        </div>
    )
};

export default PopularRecipes;
