import React, { useState, useEffect } from "react";

const PopularCategories = () => {
  const [categories, setCategories] = useState([]);
  const apiKey = '1';
  
    useEffect(() => {
            const getAllMealCategories = async () => {
            const apiUrl = `https://www.themealdb.com/api/json/v1/${apiKey}/categories.php`;

            try {
                const response = await fetch(apiUrl);

                if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const data = await response.json();

                setCategories(data.categories);
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        getAllMealCategories();
    }, []); 

  return (
    <div class="flex flex-col-2 md:h-[40vh] h-[35%] md:mt-12 mt-8 md:my-12 bg-orange-50">
      <h2>Popular Categories</h2>
      <ul>
        {categories.map((category) => (
            <div key={category.idCategory} class="flex justify-center">
                <img src={category.strCategoryThumb} class="" /> 
                <p>{category.strCategory}</p>
            </div>
        ))}
      </ul>
    </div>
  );
};

export default PopularCategories;
