import React ,{useState} from "react";
import './index.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faBagShopping, faUser , faBars ,faX } from '@fortawesome/free-solid-svg-icons';

const Nav = () => {
    const [menuIcon, setMenuIcon] = useState(faBars);
    const [menuVisible , setMenuVisible]=useState("hidden")
    const navLinks= document.getElementById("navLinks");
    const onToggleMenu = () =>{
        if (menuIcon === faBars){
            setMenuIcon(faX);
            navLinks.classList.toggle("block");
        }
        else{
            setMenuIcon(faBars);
            navLinks.classList.toggle("hidden");
        }
    }
    return (
        <nav class="h-20 bg-gray-100 no-underline flex items-center ">
            <label class="font-extrabold w-full text-left text-3xl text-[#E1611F] pl-8  ">FlavZ</label>
            <div id="navLinks" class="absolute md:static hidden md:flex  md:bg-gray-100 bg-white min-h-[60vh] md:min-h-fit left-0 top-[9%] w-full items-center px-5 ">
                <ul class="flex md:flex-row flex-col md:items-center text-center w-full text-[#373743] gap-8 md:gap-[4vw] ">
                    <li class="hover:underline hover:text-[#656C7B]"><a href="/">Recipies</a></li>
                    <li class="hover:underline hover:text-[#656C7B]"><a href="/">About Us</a></li>
                    <li class="hover:underline hover:text-[#656C7B]"><a href="/">Contact</a></li>
                </ul>
            </div>
            <ul class="flex items-center gap-[2vw] px-8 ml-28" >
                <li class="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faMagnifyingGlass} /></a></li>
                <li  class="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faBagShopping} /></a></li>
                <li class="text-[#E1611F]"><a href="#"><FontAwesomeIcon icon={faUser} /></a></li>
            </ul>
            <div>
            <label class="text-[#E1611F] list-none mr-4 cursor-pointer text-3xl md:hidden"><button onClick={onToggleMenu}><FontAwesomeIcon icon={menuIcon} /></button></label>
            </div>
        </nav>
    );
};

export default Nav;
