import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBagShopping, faUser, faBars, faX } from '@fortawesome/free-solid-svg-icons';
import classNames from 'classnames';
import styles from '../../index.css';

const Nav = ({ onScrollAboutUs, onScrollContact }) => {
  const [menuIcon, setMenuIcon] = useState(faBars);
  const [menuVisible, setMenuVisible] = useState(false);
  const [count, setCount] = useState(0);
  const [search, setSearch] = useState("");
  const [meals, setMeals] = useState([]);

  const onToggleMenu = () => {
    setMenuIcon(menuVisible ? faBars : faX);
    setMenuVisible(!menuVisible);
  };

  useEffect(() => {
    const searching = async () => {
      const apiUrl = `https://www.themealdb.com/api/json/v1/1/search.php?s=${search}`;

      try {
        const response = await fetch(apiUrl);

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const m = await response.json();

        if (search === "") {
          setMeals([]);
        } else {
          setMeals(m.meals);
        }
      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    searching();
  }, [count, search]);

  const HandleSearch = (value) => {
    setSearch(value);
    setCount(count + 1);
  };

  const handleLeaveInput = () => {
    setSearch("");
  };

  const navLinksClass = classNames('absolute', 'md:static', {
    'block': menuVisible,
    'hidden': !menuVisible,
  });

  const mealsContainerClass = classNames(
    'flex', 'flex-col', 'overflow-scroll', 'no-scrollbar', 'z-50',
    'absolute', 'md:mt-19md:mt-14', 'md:mt-10', 'mt-8', 'h-[20vh]', 'md:h-[40vh]',
    styles.mealsContainer
  );

  return (
    <nav className={`h-20 bg-white no-underline flex items-center ${menuVisible ? 'border-b-0' : ' '}  relative border-b-2`}>
      <div>
        <Link to={"/"}>
          <label className="font-extrabold w-full text-left text-4xl text-[#E1611F] pl-4 md:pl-8 cursor-pointer">FlavZ</label>
        </Link>
      </div>

      <div id="navLinks" className={navLinksClass}>
        {/* ... (rest of the code) */}
      </div>

      <ul className={`flex items-center gap-[4vw] md:gap-[3vw] ${menuVisible ? 'bg-white md:flex absolute z-20 gap-[4vw] justify-center top-[110%] left-[15vw]' : 'hidden'}`}>
        {/* ... (rest of the code) */}
      </ul>

      <div className="absolute right-5 z-20">
        <label className="text-[#E1611F] list-none mr-4 cursor-pointer text-3xl md:hidden">
          <button onClick={onToggleMenu}><FontAwesomeIcon icon={menuIcon} /></button>
        </label>
      </div>
    </nav>
  );
};

export default Nav;
