import React ,{useState ,useEffect} from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const WhatToCockToday=() =>{
    const apiKey='1';
    const [Meals, setMeals] = useState([]);
    const [category ,setCategory]=useState("");
    const [count , setCount] = useState()
    
    useEffect(() => {
        const GetMeals = async () => {
          const m =[];
          for (let i=0 ; i<10 ; i++){
            const apiUrl = "https://www.themealdb.com/api/json/v1/1/random.php";
              try {

                  const response = await fetch(apiUrl);

                  if (!response.ok) {
                  throw new Error(`HTTP error! Status: ${response.status}`);
                  }

                  const data = await response.json();

                  m.push(data.meals[0]);
              } catch (error) {
                  console.error('Error fetching data:', error.message);
              }
          } 
          setMeals(m);
        };

        GetMeals();
    }, []);
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        autoplay: false,
        autoplaySpeed: 1500,
        pauseOnHover: true,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: false
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2,
              dots:false
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              dots:false
            }
          }
        ]
      };
    return (
        <div class="md:h-[40vh] lg:h-[80vh] h-[35%] mt-12 bg-orange-50 w-[96%] ml-[2%] pt-14">
            <div>
                <p class="text-2xl">What To Cock Today </p>
                <p class="text-sm text-gray-600">Fast ,Fresh , and FoolProof</p>
            </div>
            <Slider {...settings}>
              {Meals.map( (Meal) =>(
                <div className="flex flex-row items-center justify-center">
                <img src={Meal.strMealThumb} className="h-64 w-60" alt={Meal.strMeal} />
                <div className="ml-4"> {/* Add margin for spacing */}
                  <p>{Meal.strMeal}</p>
                  <p>{Meal.strArea}</p>
                  <div class="grid grid-flow-col">
                    <button className="my-2 border w-full h-8 shadow-orange-100 cursor-pointer transition-all bg-[#E1611F] text-white px-6 py-2 rounded-lg border-orange-600 border-b-[4px] hover:brightness-110 hover:-translate-y-[1px] hover:border-b-[6px] active:border-b-[2px] active:brightness-90 active:translate-y-[2px] md:max-w-[70%] justify-center flex text-center">
                      Get The Recipe
                    </button>
                    <button className="my-2 border w-full h-8 shadow-orange-100 cursor-pointer transition-all bg-[#E1611F] text-white px-6 py-2 rounded-lg border-orange-600 border-b-[4px] hover:brightness-110 hover:-translate-y-[1px] hover:border-b-[6px] active:border-b-[2px] active:brightness-90 active:translate-y-[2px] md:max-w-[70%] justify-center flex text-center">
                      Get The Recipe
                    </button>
                  </div>
                </div>
              </div>
              

              ))}
            </Slider>
            
        </div>
    )
};
export default WhatToCockToday;
